import React, { useState } from "react";
import { processColor, Text, Dimensions, StatusBar } from "react-native";
import styled from "styled-components/native";

import Platform from "./Platform";

import G from "./assets/G";
import N from "./assets/N";
import L from "./assets/L";
import C from "./assets/C";

// const width = "80%";
// const height = "40%";

const colors = {
  black: "#010202",
  cyan: "#29AAE2",
  magenta: "#E32185",
  yellow: "#FAEA26",
};

const useForceUpdate = () => {
  const [value, setValue] = useState(0); // integer state
  return () => setValue((value) => ++value); // update the state to force render
};

const Container = styled.View`
  display: flex;
  flex: 4;
  flex-direction: ${isLandscape() ? "column" : "row"};
  align-items: stretch;
  justify-content: center;
  z-index: 0;
`;

const Row = styled.View`
  background-color: ${(props) => props.backgroundColor};
  flex: 1;
  overflow: hidden;
  align-items: stretch;
  justify-content: center;
  z-index: 1;
`;

const Letter = styled.View``;

Dimensions.addEventListener("change", () => {});

export default App = () => {
  useForceUpdate();
  return (
    <Container>
      <StatusBar hidden />
      <Row backgroundColor={colors.cyan}>
        <G as={Letter} color={processColor(colors.black)} />
      </Row>
      <Row backgroundColor={colors.magenta}>
        {/* <N as={Letter} color={processColor(colors.cyan)} /> */}
        <Text>N</Text>
      </Row>
      <Row backgroundColor={colors.yellow}>
        {/* <L as={Letter} color={processColor(colors.magenta)} /> */}
        <Text>L</Text>
      </Row>
      <Row backgroundColor={colors.black}>
        {/* <C as={Letter} color={processColor(colors.yellow)} /> */}
        <Text>C</Text>
      </Row>
    </Container>
  );
};
